mapper.update:
update tbl_feed 
set feed_like_count = (select count(feed_like_id) from tbl_feed_like e where feed_id = #{feedId})
where feed_id = #{feedId}

update tbl_reply
set reply_like_count = (select count(reply_like_id) from tbl_reply_like e where reply_id = #{replyId})
where reply_id = #{replyId}
